
function goTopEx(){var obj=document.getElementById("goTopBtn");function getScrollTop(){var scroll_top;if(document.documentElement&&document.documentElement.scrollTop){scroll_top=document.documentElement.scrollTop;}
else if(document.body){scroll_top=document.body.scrollTop;}
return scroll_top;}
function setScrollTop(value){if(document.documentElement&&document.documentElement.scrollTop){document.documentElement.scrollTop=value;}
else if(document.body){document.body.scrollTop=value;}}
$(window).addEvent('scroll',function(){getScrollTop()>0?obj.style.visibility="visible":obj.style.visibility="hidden";});obj.onclick=function(){var goTop=setInterval(scrollMove,10);function scrollMove(){setScrollTop(getScrollTop()/1.1);if(getScrollTop()<1)clearInterval(goTop);}}}